package project_java;

public class greaterLess {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 300;
		int b = 100;
		
		//condition
		if (a>b) {
			System.out.println(a+ "is greater number than " +b);
			
		} else {
			System.out.println(a+ "is less number than" +b);
			
		}
		

	}

}
